const LOCALAI_URL = process.env.LOCALAI_URL || "http://localhost:8080/v1/chat/completions";
const OPENAI_URL = "https://api.openai.com/v1/chat/completions"; // contoh tambahan

// ====== COMMAND /ai ======
bot.onText(/^\/ai(?:\s+([\s\S]+))?/, async (msg, match) => {
  const chatId = msg.chat.id;
  let input = match[1]?.trim();

  if (!input) {
    return bot.sendMessage(
      chatId,
      "âŒ Masukkan teks setelah perintah `/ai`.\n\nContoh:\n" +
        "`/ai local siapa presiden indonesia sekarang?`\n" +
        "`/ai openai jelaskan teori relativitas`",
      { parse_mode: "Markdown", reply_to_message_id: msg.message_id }
    );
  }

  // Pisahkan argumen pertama = API, sisanya = prompt
  const [apiType, ...promptArr] = input.split(" ");
  const prompt = promptArr.join(" ").trim();

  if (!prompt) {
    return bot.sendMessage(chatId, "âš ï¸ Harap masukkan pertanyaan setelah memilih API.", {
      reply_to_message_id: msg.message_id,
    });
  }

  try {
    let result = "";

    if (apiType.toLowerCase() === "local") {
      // ====== LOCALAI ======
      const res = await axios.post(
        LOCALAI_URL,
        {
          model: "gpt-4",
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7,
          max_tokens: 500,
        },
        { headers: { "Content-Type": "application/json" } }
      );
      result = res.data?.choices?.[0]?.message?.content?.trim() || "âŒ Tidak ada jawaban dari LocalAI.";
    } 
    
    else if (apiType.toLowerCase() === "openai") {
      // ====== OPENAI (contoh kalau ada key) ======
      const res = await axios.post(
        OPENAI_URL,
        {
          model: "gpt-3.5-turbo",
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7,
          max_tokens: 500,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${process.env.OPENAI_KEY}`, // simpan key di .env
          },
        }
      );
      result = res.data?.choices?.[0]?.message?.content?.trim() || "âŒ Tidak ada jawaban dari OpenAI.";
    } 
    
    else {
      return bot.sendMessage(chatId, "âš ï¸ API tidak dikenali. Gunakan: `local` atau `openai`", {
        parse_mode: "Markdown",
        reply_to_message_id: msg.message_id,
      });
    }

    await bot.sendMessage(chatId, `ğŸ’¬ *AI Response [${apiType}]*:\n\n${result}`, {
      parse_mode: "Markdown",
      disable_web_page_preview: true,
      reply_to_message_id: msg.message_id,
    });

  } catch (err) {
    console.error("âŒ Error API:", err.response?.data || err.message);
    bot.sendMessage(chatId, "âŒ Terjadi kesalahan saat memproses permintaan.", {
      reply_to_message_id: msg.message_id,
    });
  }
});
