bot.onText(/^\/play(?:\s+(.+))?$/, async (msg, match) => {
  const chatId = msg.chat.id;
  let query = (match[1] || "").trim();

  if (!query) {
    return bot.sendMessage(
      chatId,
      "âš ï¸ Gunakan format:\n/play judul lagu\n/play judul lagu -v (untuk video)",
      { reply_to_message_id: msg.message_id }
    );
  }

  // Deteksi mode video
  const isVideo = query.endsWith("-v");
  if (isVideo) query = query.replace(/-v$/, "").trim();

  let progressMsg;
  try {
    // Progress awal
    progressMsg = await bot.sendMessage(chatId, "ğŸ” Mencari video...", {
      reply_to_message_id: msg.message_id,
    });

    // Pencarian YouTube
    const searchRes = await axios.get(
      `https://api.siputzx.my.id/api/s/youtube?query=${encodeURIComponent(query)}`
    );
    const results = searchRes.data?.data;

    if (!results || !results.length) {
      await bot.editMessageText("âŒ Tidak ada hasil ditemukan.", {
        chat_id: chatId,
        message_id: progressMsg.message_id,
      });
      return;
    }

    const video = results[0];

    // Update progress
    await bot.editMessageText(
      isVideo ? "â³ Mengunduh video..." : "â³ Mengunduh audio...",
      {
        chat_id: chatId,
        message_id: progressMsg.message_id,
      }
    );

    // API download
    const apiUrl = isVideo
      ? `https://restapi-v2.simplebot.my.id/download/ytmp4?url=${encodeURIComponent(video.url)}`
      : `https://restapi-v2.simplebot.my.id/download/ytmp3?url=${encodeURIComponent(video.url)}`;

    const mediaRes = await axios.get(apiUrl);
    const mediaUrl = mediaRes.data?.result;

    if (!mediaUrl) {
      await bot.editMessageText("âŒ Gagal mengambil media.", {
        chat_id: chatId,
        message_id: progressMsg.message_id,
      });
      return;
    }

    // Caption info (pakai HTML aman, tanpa <blockquote>)
    const caption = `
<b>ğŸµ ${video.title}</b>
ğŸ‘¤ ${video.author?.name || "Unknown"}
â±ï¸ ${video.duration?.timestamp || "-"}
ğŸ‘ï¸ ${video.views} views
ğŸ“… ${video.ago}
`;

    // Sanitasi nama file
    const safeTitle = video.title.replace(/[<>:"/\\|?*]+/g, "");
    const ext = isVideo ? "mp4" : "mp3";
    const tmpFile = path.join(__dirname, `${Date.now()}-${safeTitle}.${ext}`);

    // Download file sementara
    const response = await axios({
      method: "get",
      url: mediaUrl,
      responseType: "stream",
    });

    await new Promise((resolve, reject) => {
      const stream = response.data.pipe(fs.createWriteStream(tmpFile));
      stream.on("finish", resolve);
      stream.on("error", reject);
    });

    // Kirim ke Telegram
    if (isVideo) {
      await bot.sendVideo(chatId, tmpFile, {
        caption,
        parse_mode: "HTML",
        reply_to_message_id: msg.message_id,
      });
    } else {
      await bot.sendAudio(chatId, tmpFile, {
        title: video.title,
        performer: video.author?.name || "Unknown",
        caption,
        parse_mode: "HTML",
        reply_to_message_id: msg.message_id,
      });
    }

    // Hapus file sementara + pesan progress
    fs.unlinkSync(tmpFile);
    bot.deleteMessage(chatId, progressMsg.message_id).catch(() => {});
  } catch (err) {
    console.error(err.response?.data || err.message);

    if (progressMsg) {
      await bot
        .editMessageText("âŒ Terjadi kesalahan, coba lagi nanti.", {
          chat_id: chatId,
          message_id: progressMsg.message_id,
        })
        .catch(() => {});
      setTimeout(() => {
        bot.deleteMessage(chatId, progressMsg.message_id).catch(() => {});
      }, 5000);
    } else {
      bot.sendMessage(chatId, "âŒ Terjadi kesalahan, coba lagi nanti.", {
        reply_to_message_id: msg.message_id,
      });
    }
  }
});

